(94, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-07-10 16:14:22', NULL, 0, 'plain', '', 0, 0, 'info'),
(95, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-07-13 14:16:45', NULL, 0, 'plain', '', 0, 0, 'info'),
(96, 'square_payments_q3_2023', 'marketing', 'en_US', 'Square for WooCommerce', 'Connect your Square account with WooCommerce to accept online and in-person payments (including contactless payments and cross-channel gift cards) quickly and securely. Save time by syncing sales, customers, and items and inventory — and manage everything through one centralized platform.', '{}', 'pending', 'woocommerce.com', '2023-07-22 21:45:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(97, 'wc-admin-woopayments-rebrand', 'marketing', 'en_US', 'WooCommerce Payments is now WooPayments', 'We’ve changed the name as part of recent updates to the Woo brand. You’ll still enjoy the same seamless payments experience — just look for WooPayments in your WordPress admin.', '{}', 'pending', 'woocommerce.com', '2023-08-04 01:45:58', NULL, 0, 'plain', '', 0, 0, 'info'),
