(102, 'tiktok-apac-webinar-2023', 'marketing', 'en_US', 'Unlocking TikTok: Live Webinar August 9', 'Join us on August 9 for a free online masterclass with our partners at TikTok, tailored to Woo merchants in the APAC region (11 am AEST). You’ll learn how to connect your store to TikTok, unlock expert advertising tools, and reach its one billion active global users — all from the comfort of your own home.', '{}', 'pending', 'woocommerce.com', '2023-08-05 10:54:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(103, 'wc-admin-manage-orders-on-the-go', 'info', 'en_US', 'Manage your orders on the go', 'Look for orders, customer info, and process refunds in one click with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-15 23:12:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(104, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-08-15 23:12:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(105, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-08-27 00:17:58', NULL, 0, 'plain', '', 0, 0, 'info'),
(106, 'tiktok_shopGTM_q3-2023_noTikTok', 'marketing', 'en_US', 'You can now sell your products on TikTok Shop', 'TikTok is changing the way shoppers find and connect with brands. With TikTok Shop, you can sync your entire catalog from within Woo and sell directly to TikTok users through in-feed videos, livestreams, and your product showcase tab.', '{}', 'pending', 'woocommerce.com', '2023-09-08 00:10:59', NULL, 0, 'plain', '', 0, 0, 'info'),
(107, 'klaviyo_q3_2023', 'marketing', 'en_US', 'Build long lasting relationships with Klaviyo for WooCommerce', 'Increase customer engagement and boost conversions with powerful email and SMS automation from Klaviyo. Fully integrated with your Woo store, Klaviyo enables you to send personalized communications to your customers — ensuring that the right message is delivered at the right time for maximum impact.', '{}', 'unactioned', 'woocommerce.com', '2023-09-14 09:56:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(108, 'wc_admin_BNPL_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay and Affirm have arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay and Affirm — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-21 15:03:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(109, 'wc_admin_afterpay_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay has arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-21 15:03:48', NULL, 0, 'plain', '', 0, 0, 'info'),
